package com.cogent;

public class FindBigNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 24;
		int j = 15;
		
		if (i > j) {
			System.out.println(i + " is a larger number than " + j);
		}
		else {
			System.out.println(j + " is a larger number than " + i);
		}

	}

}
